
type Position = [number, number];
function nearestExit(maze: string[][], entrance: Position): number {
    const m = maze.length;
    const n = maze[0].length;
    function isExitReached([x, y]: Position) {
        return (x === 0 || y === 0 || x === m - 1 || y === n - 1) &&
            !(x === entrance[0] && y === entrance[1])
    }
    function getNextPossibleMoves([x, y]: Position, steps = 0) {
        const moves: [Position, number][] = [];
        const dirs = [[1, 0], [-1, 0], [0, 1], [0, -1]];
        for (const [dx, dy] of dirs) {
            const nx = x + dx;
            const ny = y + dy;

            if (
                nx >= 0 && ny >= 0 &&
                nx < m && ny < n &&
                maze[nx][ny] === '.'
            ) {
                maze[nx][ny] = '+';
                moves.push([[nx, ny], steps]);
            }
        }
        return moves
    }
    const moves = getNextPossibleMoves(entrance)
    maze[entrance[0]][entrance[1]] = '+';

    for (const [[i, j], steps] of moves) {
        if (isExitReached([i, j])) return steps + 1;
        moves.push(...getNextPossibleMoves([i, j], steps + 1))
    }
    return -1
};

